Shows added a fallback to bitmap rendering and changing type of warnings about unsupported metafile records. voidHandleBinaryRasterWarnings()
{
autodoc=MakeObject<Document>(MyDir+u"WMFwithimage.docx");

autometafileRenderingOptions=MakeObject<MetafileRenderingOptions>();

//Setthe"EmulateRasterOperations"propertyto"false"tofallbacktobitmapwhen
//itencountersametafile,whichwillrequirerasteroperationstorenderintheoutputPDF.
metafileRenderingOptions->set_EmulateRasterOperations(false);

//Setthe"RenderingMode"propertyto"VectorWithFallback"totrytorendereverymetafileusingvectorgraphics.
metafileRenderingOptions->set_RenderingMode(MetafileRenderingMode::VectorWithFallback);

//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDFandappliestheconfiguration
//inourMetafileRenderingOptionsobjecttothesavingoperation.
autosaveOptions=MakeObject<PdfSaveOptions>();
saveOptions->set_MetafileRenderingOptions(metafileRenderingOptions);

autocallback=MakeObject<ExPdfSaveOptions::HandleDocumentWarnings>();
doc->set_WarningCallback(callback);

doc->Save(ArtifactsDir+u"PdfSaveOptions.HandleBinaryRasterWarnings.pdf",saveOptions);

ASSERT_EQ(1,callback->Warnings->get_Count());
ASSERT_EQ(u"'R2_XORPEN'binaryrasteroperationispartlysupported.",callback->Warnings->idx_get(0)->get_Description());
}

classHandleDocumentWarnings:publicIWarningCallback
{
public:
SharedPtr<WarningInfoCollection>Warnings;

voidWarning(SharedPtr<WarningInfo>info)override
{
if(info->get_WarningType()==WarningType::MinorFormattingLoss)
{
std::cout<<(String(u"Unsupportedoperation:")+info->get_Description())<<std::endl;
Warnings->Warning(info);
}
}

HandleDocumentWarnings():Warnings(MakeObject<WarningInfoCollection>())
{
}
};

