autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"Helloworld!");

autoencryptionDetails=MakeObject<PdfEncryptionDetails>(u"password",String::Empty);

//Startbydisallowingallpermissions.
encryptionDetails->set_Permissions(PdfPermissions::DisallowAll);

//Extendpermissionstoallowtheeditingofannotations.
encryptionDetails->set_Permissions(PdfPermissions::ModifyAnnotations|PdfPermissions::DocumentAssembly);

//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDF.
autosaveOptions=MakeObject<PdfSaveOptions>();

//Enableencryptionviathe"EncryptionDetails"property.
saveOptions->set_EncryptionDetails(encryptionDetails);

//Whenweopenthisdocument,wewillneedtoprovidethepasswordbeforeaccessingitscontents.
doc->Save(ArtifactsDir+u"PdfSaveOptions.EncryptionPermissions.pdf",saveOptions);

